import logo from './logo.svg';
import './App.css';
import React,{ Component } from 'react';

class App extends Component {

state={togButton:false}
handler=()=>
{
  const checkstate=this.state.togButton;
  this.setState({togButton:!checkstate})

  var dvPassport = document.getElementById("toggleCheckBox").innerText;
  alert(dvPassport);

}

  render(){


    return (
    <div >
      <h1 className="text-center">This is Image Toggle App</h1>


{
this.state.togButton?
<div className="text-center">
<img style={{marginTop:"1%"}} src="Assests/musk1.jpg" width="300dp" height="300dp"/>
</div>:

<div className="text-center">
<img style={{marginTop:"1%"}} src="Assests/musk2.jpg" width="300dp" height="300dp"/>
</div>

}



      <div className="text-center" style={{marginTop:"1%"}}>
        <label className="switch">
  <input type="checkbox" id="toggleCheckBox" checked onClick={this.handler}/>
  <span class="slider round"></span>
      </label>
        </div>
    </div>
     );
}}

export default App;
